package Operator;

public class boolean1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		boolean b1=true;
		boolean b2=false;
		System.out.println(b1&b1);
		System.out.println(b1&b2);
		System.out.println(b2&b1);
		System.out.println(b2&b2);
		System.out.println("...................");
		
		System.out.println(b1|b2);
		System.out.println(b1|b2);
		System.out.println(b2|b1);
		System.out.println(b2|b2);
		System.out.println("...................");
		System.out.println(b1^b1);
		System.out.println(b1^b2);
		System.out.println(b2^b1);
		System.out.println(b2^b2);

	}

}
